using System.Security.Cryptography;
using Dex.ResponseSigning.KeyExtractor;
using Moq;

namespace Dex.ResponseSigning.Tests.Mocks;

internal class PrivateKeyExtractorMock : Mock<IPrivateKeyExtractor>
{
    private const string PrivateKey =
        "-----BEGIN RSA PRIVATE KEY-----\\n-----END RSA PRIVATE KEY-----";

    public PrivateKeyExtractorMock()
    {
        var rsa = RSA.Create();
        rsa.ImportFromPem(PrivateKey.ToCharArray());

        Setup(x => x.GetKey()).Returns(rsa);
    }
}